package com.iot.calcvirtualpoint.common.util;import org.apache.commons.lang3.StringUtils;import com.exue.framework.util.GenerateUtil;import com.exue.framework.util.MD5Util;/** * 密码加密生产工具类 * */public class EncryptUtil{	/**	 * @Title 密码加密	 * @Description 生成密码方式 0为系统本身使用	 * @param pwd	 * @param salt	 * @return	 */	public static final String encodePwd(String pwd,String salt){		if(!StringUtils.isBlank(pwd) && !StringUtils.isBlank(salt)){		    String _pwd = pwd;            if (32 != pwd.length()) {                _pwd = MD5Util.MD5(pwd);            }            _pwd = _pwd.concat(salt);            return MD5Util.MD5(_pwd);		}		return "";	}		/**	 * 生成4位含有数字大小写字母的随机数	 * @return	 */	public static final String genSalt(){		return GenerateUtil.getRandomStr(4);	}	/**	 * 生成10位含有数字大小写字母的随机数	 * @return	 */	public static final String genPassword(){        return GenerateUtil.getRandomStr(10);    }		public static void  main(String[] args){		String pwd = encodePwd("asdfasdf","aaaa");		System.out.println(pwd);	    pwd = encodePwd("aaaaaa","aaa");		System.out.println(pwd);	}}